import React, { Component } from 'react';
// import logo from '../logo.png';
import EthSwap from '../../src/abis/EthSwap.json';
import Token   from '../../src/abis/Token.json';
import Navbar  from './Navbar';
import Web3 from 'web3';
import './App.css';
// import NavbarHeader from 'react-bootstrap/lib/NavbarHeader';

class App extends Component {

  
  async componentWillMount(){

    await this.loadWeb3();
    await this.loadBlockChainData();
  }
  
  async loadBlockChainData(){
     
    var accounts;
    const web3 = await window.web3;      
      accounts = await web3.eth.getAccounts();        
    this.setState({account: accounts[0]});

    const ethBalance = await web3.eth.getBalance(this.state.account);
    //this.setState({ethBalance:ethBalance})
    console.log(ethBalance)
    this.setState({ethBalance});

    const networkId = await web3.eth.getId();
    const abi = Token.abi;
    const tokenData = await Token.network[networkId];

    if(tokenData){

      const token = await web3.eth.Contract(token.abi,tokenData.address);
      console.log(token);
    }else{

      window.alert('token contract not deployed to the detected network !');
    }
  
   
    console.log(this.setState.account);
    console.log(this.setState.ethBalance);
    
    return accounts;  
  }

  async loadWeb3(){

/*
    let ethereum = window.ethereum;
    //web3 = window.web3;
 
    if (typeof ethereum !== 'undefined') {

        await ethereum.enable();
        web3 = new Web3(ethereum);
    } else if (typeof web3 !== 'undefined') {

        web3 = new Web3(web3.currentProvider);
    } else {

        web3 = new Web3(new Web3.providers.HttpProvider(process.env.WEB3_PROVIDER));
    }  

  */
   
    let ethereum = await window.ethereum;   

    if(typeof ethereum !== 'undefined'){ 
        
        
        await ethereum.enable();         
        window.web3 = await new Web3(ethereum);       
        window.alert('web3 instance injected, using Local web3');

    }else{
      
        window.alert('Non-Ethereum browser, detected install Metamask !');
       // Fallback to localhost if no web3 injection. We've configured this to
       // use the development console's port by default.
     
        var provider = new Web3.providers.HttpProvider('http://127.0.0.1:7545')
        window.web3  = new Web3(provider)  
        window.alert('configured to  port by default: http://127.0.0.1:7545');              
    }     
  }

  constructor(props){

    super(props)
    this.state = {

      account:'',
      ethBalance:'0'
    }
  }

  render() {
    return (
      <div>
        <Navbar account = {this.state.account}/>        
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
              EthSwap
              <ul c>

              </ul>
              <p>{this.props.account}</p>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
